import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#8257e5',
		justifyContent: 'center',
		padding: 40,
		width: '100%',
		height: '100%'
	},
	banner: {
		width: '100%',
		resizeMode: 'contain'
	}
});

export default styles;
