import React, { useState } from 'react';
import PageHeader from '../../components/PageHeader';
import Input from '../../components/Input';
import warningIcon from '../../assets/images/icons/warning.svg';
import Textarea from '../../components/textarea';
import Select from '../../components/Select';
import './styles.css';

function TeacherForm() {
	//o react so fica observando a variável se usarmos o conceito de state
	// o useState passa um array onde o primeiro item são os dados
	// fazemos uma desestruturação da variável scheduleItems para [scheduleItems]
	// AS VARIÁVEIS CRIADAS NO REACT NÃO SÃO MODIFICÁVEIS

	const [
		name,
		setName
	] = useState('');
	const [
		avatar,
		setAvatar
	] = useState('');
	const [
		whatsapp,
		setWhatsapp
	] = useState('');
	const [
		bio,
		setBio
	] = useState('');

	const [
		scheduleItems,
		setScheduleItems
	] = useState([
		{ week_day: 0, from: '', to: '' }
	]);

	function addNewScheduleItem() {
		setScheduleItems([
			...scheduleItems,
			{ week_day: 0, from: '', to: '' }
		]);
	}

	return (
		<div id="page-teacher-form" className="container">
			<PageHeader
				title="Que incrível que você quer dar aulas"
				description="O primeiro passo: preencha o formulário de inscrição"
			/>
			<main>
				<fieldset>
					<legend>Os seus dados</legend>
					<Input
						name="name"
						label="Nome Completo"
						value={name}
						onChange={(e) => {
							setName(e.target.value);
						}}
					/>
					<Input
						name="avatar"
						label="Avatar"
						value={avatar}
						onChange={(e) => {
							setAvatar(e.target.value);
						}}
					/>
					<Input
						name="whatsapp"
						label="WhatsApp"
						value={whatsapp}
						onChange={(e) => {
							setWhatsapp(e.target.value);
						}}
					/>
					<Textarea
						name="bio"
						label="biografia"
						value={bio}
						onChange={(e) => {
							setName(e.target.value);
						}}
					/>
				</fieldset>
				<fieldset>
					<legend>Sobre a aula</legend>
					<Select
						name="subject"
						label="Matéria"
						options={[
							{ value: 'Artes', label: 'Artes' },
							{ value: 'Biologia', label: 'Biologia' },
							{ value: 'Ciências', label: 'Ciências' },
							{ value: 'Educação Física', label: 'Educação Física~' },
							{ value: 'Geografia', label: 'Geografia' }
						]}
					/>
					<Input name="cost" label="Custo da sua hora por aula" />
				</fieldset>
				<fieldset>
					<legend>
						Horários Disponíveis
						<button type="button" onClick={addNewScheduleItem}>
							+ Novo Horário
						</button>
					</legend>
					{scheduleItems.map((scheduleItem) => {
						return (
							<div className="schedule-item" key={scheduleItem.week_day}>
								<Select
									name="week-day"
									label="Dia da Semana"
									options={[
										{ value: '0', label: 'domingo' },
										{ value: '1', label: 'segunda-feira' },
										{ value: '2', label: 'terça-feira' },
										{ value: '3', label: 'quarta-feira' },
										{ value: '4', label: 'quinta-feira' },
										{ value: '5', label: 'sexta-feira' },
										{ value: '6', label: 'sábado' }
									]}
								/>
								<Input name="from" label="Das" type="time" />
								<Input name="to" label="Até" type="time" />
							</div>
						);
					})}
				</fieldset>
				<footer>
					<p>
						<img src={warningIcon} alt="Aviso Importante" />
						Importante! <br />
						Preencha todos os dados
					</p>
					<button type="button">Gravar</button>
				</footer>
			</main>
		</div>
	);
}

export default TeacherForm;
